CREATE TABLE `staff_perform_project_data` ( `sppd_id` INT(11) NOT NULL AUTO_INCREMENT , `sppd_empid` INT(11) NOT NULL , `sppd_ptitle` VARCHAR(255) NOT NULL , `sppd_prole` VARCHAR(255) NOT NULL , `sppd_pfundagency` VARCHAR(255) NOT NULL , `sppd_agendytype` VARCHAR(255) NOT NULL , `sppd_budget` FLOAT(10,2) NOT NULL , `sppd_duration` INT(3) NOT NULL , `sppd_fromdate` DATE NOT NULL , `sppd_todate` DATE NOT NULL , `sppd_remark` VARCHAR(255) NOT NULL , `sppd_creatorname` VARCHAR(255) NOT NULL , `sppd_creationdate` VARCHAR(255) NOT NULL , `sppd_modifiername` VARCHAR(255) NOT NULL , `sppd_modifidate` TIMESTAMP NOT NULL , PRIMARY KEY (`sppd_id`)) ENGINE = InnoDB;

CREATE TABLE `staff_perform_award_data` ( `spad_id` INT(11) NOT NULL PRIMARY KEY AUTO_INCREMENT, `spad_empid` INT(11) NOT NULL , `spad_awardtitle` VARCHAR(255) NOT NULL , `spad_awardtype` VARCHAR(255) NOT NULL , `spad_year` INT(4) NOT NULL , `spad_awardby` VARCHAR(255) NOT NULL , `spad_details` VARCHAR(255) NOT NULL , `spad_creatorname` VARCHAR(255) NOT NULL , `spad_creationdate` DATE NOT NULL , `spad_modifiername` VARCHAR(255) NOT NULL , `spad_modifidate` TIMESTAMP NOT NULL ) ENGINE = InnoDB;
