CREATE TABLE `staff_pub_data` ( `spbd_id` INT(11) NOT NULL AUTO_INCREMENT , `spbd_empid` INT(9) NOT NULL , `spbd_pubtype` VARCHAR(255) NOT NULL , `spbd_title` VARCHAR(255) NOT NULL , `spbd_authors` VARCHAR(255) NOT NULL , `spbd_authortype` VARCHAR(255) NOT NULL , `spbd_journalname` VARCHAR(255) NULL , `spbd_month` VARCHAR(50) NULL , `spbd_year` INT(4) NULL , `spbd_issnno` VARCHAR(255) NULL , `spbd_chapno` INT(3) NULL , `spbd_pageno` INT(3) NULL , `spbd_metrictype` VARCHAR(255) NULL , `spbd_metricvalue` INT(5) NULL , `spbd_publevel` VARCHAR(255) NULL , `spbd_progrmname` VARCHAR(255) NULL , `spbd_progrmvenue` VARCHAR(255) NULL , `spbd_bookname` VARCHAR(255) NULL , `spbd_bookchapternme` VARCHAR(255) NULL , `spbd_publishername` VARCHAR(255) NULL , `spbd_language` VARCHAR(255) NULL , `spbd_creatorname` VARCHAR(255) NOT NULL , `spbd_creationdate` DATE NOT NULL , `spbd_modifiername` VARCHAR(255) NOT NULL , `spbd_modifidate` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`spbd_id`)) ENGINE = InnoDB;

CREATE TABLE `staff_training_attended` ( `sta_id` INT(11) NOT NULL AUTO_INCREMENT , `sta_empid` INT(11) NOT NULL , `sta_prgtype` VARCHAR(255) NOT NULL , `sta_prgsubtype` VARCHAR(255) NULL , `sta_prglevel` VARCHAR(255) NULL , `sta_prgtitle` VARCHAR(255) NULL , `sta_prgduration` INT(2) NULL , `sta_prgfrmdate` DATE NULL , `sta_prgtodate` DATE NULL , `sta_prgvenue` VARCHAR(255) NULL , `sta_prgorganisedby` VARCHAR(255) NULL , `sta_sponceredby` VARCHAR(255) NULL , `sta_creatorname` VARCHAR(255) NOT NULL , `sta_creationdate` DATE NOT NULL , `sta_modifiername` VARCHAR(255) NOT NULL , `sta_modifierdate` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`sta_id`)) ENGINE = InnoDB;

CREATE TABLE `staff_training_organised` ( `sto_id` INT(11) NOT NULL AUTO_INCREMENT , `sto_empid` INT(11) NOT NULL , `sto_prgtype` VARCHAR(255) NOT NULL , `sto_prgsubtype` VARCHAR(255) NULL , `sto_prglevel` VARCHAR(255) NULL , `sto_prgtitle` VARCHAR(255) NULL , `sto_prgduration` INT(2) NULL , `sto_prgfrmdate` DATE NULL , `sto_prgtodate` DATE NULL , `sto_prgvenue` VARCHAR(255) NULL , `sto_prgorganisedby` VARCHAR(255) NULL , `sto_sponceredby` VARCHAR(255) NULL ,`sto_capacity` VARCHAR(255) NULL ,`sto_participantno` VARCHAR(255) NULL ,`sto_participantnature` VARCHAR(255) NULL , `sto_creatorname` VARCHAR(255) NOT NULL , `sto_creationdate` DATE NOT NULL , `sto_modifiername` VARCHAR(255) NOT NULL , `sto_modifierdate` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`sto_id`)) ENGINE = InnoDB 
